// Mobile Segmented Control Style
export const MobileSegmentedControl: Story = {
  parameters: {
    viewport: {
      defaultViewport: 'mobile1',
    },
  },
  render: () => (
    <Tabs defaultValue="day">
      <TabsList className="rounded-lg bg-gray-100 p-1">
        <div className="grid w-full grid-cols-3 gap-1">
          {['day', 'week', 'month'].map(period => (
            <Tab
              key={period}
              value={period}
              className="rounded-md py-2 text-sm data-[state=active]:bg-white data-[state=active]:shadow-sm"
            >
              {period.charAt(0).toUpperCase() + period.slice(1)}
            </Tab>
          ))}
        </div>
      </TabsList>
      <TabsContent value="day">
        <div className="p-4">
          <h3 className="text-lg font-medium">Vista Diaria</h3>
          <div className="mt-2 space-y-2">
            {['9:00 AM', '2:00 PM', '5:00 PM'].map(time => (
              <div key={time} className="rounded bg-gray-50 p-2">
                <span className="text-sm text-gray-600">{time}</span>
              </div>
            ))}
          </div>
        </div>
      </TabsContent>
      <TabsContent value="week">
        <div className="p-4">
          <h3 className="text-lg font-medium">Vista Semanal</h3>
          <div className="mt-2 grid grid-cols-7 gap-1">
            {['L', 'M', 'X', 'J', 'V', 'S', 'D'].map(day => (
              <div key={day} className="text-center text-sm text-gray-600">
                {day}
              </div>
            ))}
          </div>
        </div>
      </TabsContent>
      <TabsContent value="month">
        <div className="p-4">
          <h3 className="text-lg font-medium">Vista Mensual</h3>
          <p className="mt-2 text-sm text-gray-600">Calendario del mes actual</p>
        </div>
      </TabsContent>
    </Tabs>
  ),
}
